select
g.goods_id, g.cat_id, g.goods_sn, g.goods_name, g.goods_name_style, g.click_count, g.brand_id, g.provider_name, g.goods_number, g.goods_weight, g.market_price, g.shop_price, g.cost_price, g.promote_price, g.promote_start_date, g.promote_end_date, g.warn_number, g.keywords, g.goods_brief, g.act_desc, g.goods_thumb, g.goods_img, g.original_img, g.is_real, g.extension_code, g.is_on_sale, g.is_alone_sale, g.is_shipping, g.integral, g.create_date, g.sort_order, g.is_delete, g.is_best, g.is_new, g.is_hot, g.is_promote, g.is_special, g.bonus_type_id, g.last_update, g.goods_type, g.seller_note, g.give_integral, g.rank_integral, g.business_id, g.is_check, g.tb_iid, g.is_export, g.user_id, g.store_id, g.exchangeRate, g.profit, g.shipping, g.profitType, g.currency, g.goods_url, g.tabbox
from hai_favorites f left join hai_goods g on g.goods_id = f.goods_id where f.user_id = #{user_id} and g.goods_id is not null order by f.add_time desc limit #{limitStart,jdbcType=INTEGER},#{limitEnd,jdbcType=INTEGER}

	select

